import { fetchMatch } from "api/apis";
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import styled from "styled-components";
import { useSelector } from "react-redux";
import {
  Container,
  TodayMatch,
  TextWrapper,
  IframeContainer,
  ImageContainer,
  Logo,
  MatchCard,
  Name,
  RedTextContainer,
  Team,
  TimingOrScore,
  Title,
  Wrapper,
} from "./match.style";

const Match = () => {
  const { idMatch } = useParams();
  const [match, setMAtch] = useState([]);
  const [m, setM] = useState([]);

  const data = useSelector((state) => state.raw_data);

  console.log(
    "datasss",
    data.raw_data.filter((m) => m.id === idMatch)[0]
  );
  useEffect(() => {
    if (data) {
      set(
        data.raw_data.filter((m) => m.id === idMatch)[0]
        );
    }
    console.log("data", data);
  }, []);

  const Iframe = styled.iframe`
    padding-right: 20px;
    z-index: 0;
    margin-top: -220px;
  `;
  console.log("m", m);
  if (m && data) {
    console.log(m);
    return (
      <Container>
        <TodayMatch>
          <TextWrapper>
            <Title>الصفحة الخاصة بنقل مباراة</Title>
          </TextWrapper>
        </TodayMatch>
        <Wrapper>
          <MatchCard>
            <Team>
              <ImageContainer>
                <Logo src={m.details.image_team2} />
              </ImageContainer>
              <Name direction="left">{m.details.team2}</Name>
            </Team>

            <TimingOrScore>{m.details.state}</TimingOrScore>
            <Team>
              <Name direction="left">{m.details.team1}</Name>
              <ImageContainer>
                <Logo src={m.details.image_team1} />
              </ImageContainer>
            </Team>
          </MatchCard>
          <IframeContainer>
            <div id="PlaceToPutTable"></div>
            {/* <iframe             allowfullscreen="true"  height="500px"
              scrolling=""
                          width="100%"

   id="iframe" src="https://beinmatch.ma/home/live/11785/1/برشلونة_vs_فياريال" style="display:hidden;"></iframe> */}
            <Iframe
              height="800px"
              id="iframe"
              allowfullscreen="true"
              frameborder="0"
              scrolling=""
              // src={match.Link}
              width="100%"
              src={m.details.match_link}
              scrolling="no"
            ></Iframe>
          </IframeContainer>
        </Wrapper>

        {/* <RedTextContainer>
          Disclaimer: This content is provided and hosted by Another Website. Be
          in Match is not responsible for and cannot be accountable for any of the
          content hosted on the third-party site. For any legal complaints please
          contact the video/audio hosting provider.
        </RedTextContainer>
        <RedTextContainer>
          <Link to={`/`}>في حالة توقف البث إضغط هنا لتحديث المشغل</Link>
        </RedTextContainer> */}
      </Container>
    );
  } else {
    return <div>...loading</div>;
  }
};
export default Match;
