import { fetchMatch } from "api/apis";
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import styled from "styled-components";
import { useSelector } from "react-redux";
import {
  Container,
  TodayMatch,
  TextWrapper,
  IframeContainer,
  ImageContainer,
  Logo,
  MatchCard,
  Name,
  RedTextContainer,
  Team,
  TimingOrScore,
  Title,
  Wrapper,
} from "./match.style";

const Match = () => {
  const { idMatch } = useParams();
  const [match, setMAtch] = useState([]);

  const data = useSelector((state) => state.raw_data);
  const m = data.raw_data.filter((m) => m.id === idMatch);
  console.log(m);
  // useEffect(() => {
  //   const sendrequest = async () => {
  //     const match = await fetchMatch(idMatch);
  //     setMAtch(match);
  //   };
  //   sendrequest();
  // }, [idMatch]);

  // if (!match) {
  //   return <Container>No Match Founded!</Container>;
  // }

  const Iframe = styled.iframe`
    padding-right: 20px;
    z-index: 0;
    margin-top: -220px;
  `;

  if(m){
    return (
      <Container>
        <TodayMatch>
          <TextWrapper>
            <Title>الصفحة الخاصة بنقل مباراة</Title>
          </TextWrapper>
        </TodayMatch>
        <Wrapper>
          <MatchCard>
            <Team>
            {/* ScoreTeam1={item.details.score_team1}
                LogoTeam1={item.details.image_team1}
                Team1={item.details.team1}
                LogoTeam2={item.details.image_team2}
                Team2={item.details.team2}
                ScoreTeam2={item.details.score_team2}
                Time={item.details.state}
                Type={item.details.match_type}
                State={item.details.state} */}
              <ImageContainer>
                <Logo src={m.details.image_team2} />
              </ImageContainer>
              <Name direction="left">{m.details.team2}</Name>
            </Team>

            <TimingOrScore>{m.details.state}</TimingOrScore>
            <Team>
              <Name direction="left">{m.details.team1}</Name>
              <ImageContainer>
                <Logo src={m.details.image_team1}/>
              </ImageContainer>
            </Team>
          </MatchCard>
          <IframeContainer>
            <div id="PlaceToPutTable"></div>
            {/* <iframe             allowfullscreen="true"  height="500px"
              scrolling=""
                          width="100%"

   id="iframe" src="https://beinmatch.ma/home/live/11785/1/برشلونة_vs_فياريال" style="display:hidden;"></iframe> */}
            <Iframe
              height="800px"
              id="iframe"
              allowfullscreen="true"
              frameborder="0"
              scrolling=""
              // src={match.Link}
              width="100%"
              src={
                m.details.match_link
              }
              scrolling="no"
            ></Iframe>
          </IframeContainer>
        </Wrapper>

        {/* <RedTextContainer>
          Disclaimer: This content is provided and hosted by Another Website. Be
          in Match is not responsible for and cannot be accountable for any of the
          content hosted on the third-party site. For any legal complaints please
          contact the video/audio hosting provider.
        </RedTextContainer>
        <RedTextContainer>
          <Link to={`/`}>في حالة توقف البث إضغط هنا لتحديث المشغل</Link>
        </RedTextContainer> */}
      </Container>
    );
  }else {
   return  <div>...loading</div>
  }

};
export default Match;
